package assignment;

public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 22, b = 12, add, sub, div, mul, mod;
		add = a + b;
		sub = a - b;
		div = a / b;
		mul = a * b;
		mod = a % b;
		System.out.println("add=>" + add + "sub=>" + sub + "div=>" + div + "mul=>" + mul + "mod=>" + mod);

	}

}
